import React, { useState } from 'react';
import CardBody from './CardBody';

// TODO: Add a comment explaining what export default does
// when importing this file this function is exported unless specified otherwise
export default function Counter() {
  // TODO: Add a comment that explains how the useState hook works
  // useState is used to declare a state variable
  let [count, setCount] = useState(0);

  // TODO: Explain what is happening with this click handler
  // This function when called uses the setCount method from the "count" state variable to update it's value
  const handleIncrement = () => {
    setCount((count + 1));
  };

  // TODO: Explain what is happening with this click handler
  // Same thing as above but it's decreasing the value
  const handleDecrement = () => {
    setCount((count - 1));
  };

  return (
    <div className="card text-center">
      <div className="card-header bg-primary text-white">Click Counter!</div>
      {/* TODO: Add a comment that explains what props are getting passed to CardBody */}
      {/* the state variable count and the methods use to decrease and increase the value */}
      <CardBody
        count={count}
        handleIncrement={handleIncrement}
        handleDecrement={handleDecrement}
      />
    </div>
  );
}
